
function decode(base64: string): Uint8Array {
  const binaryString = atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes;
}

async function decodeAudioData(
  data: Uint8Array,
  ctx: AudioContext,
  sampleRate: number,
  numChannels: number,
): Promise<AudioBuffer> {
  // Fix: Corrected typo from Int1TArray to Int16Array.
  const dataInt16 = new Int16Array(data.buffer);
  const frameCount = dataInt16.length / numChannels;
  const buffer = ctx.createBuffer(numChannels, frameCount, sampleRate);

  for (let channel = 0; channel < numChannels; channel++) {
    const channelData = buffer.getChannelData(channel);
    for (let i = 0; i < frameCount; i++) {
      channelData[i] = dataInt16[i * numChannels + channel] / 32768.0;
    }
  }
  return buffer;
}

let audioContext: AudioContext | null = null;
let currentSource: AudioBufferSourceNode | null = null;

const getAudioContext = (): AudioContext => {
    if (!audioContext || audioContext.state === 'closed') {
        audioContext = new (window.AudioContext || (window as any).webkitAudioContext)({
            sampleRate: 24000
        });
    }
    return audioContext;
};

export const stopCurrentAudio = () => {
    if (currentSource) {
        currentSource.onended = null;
        try {
          currentSource.stop();
        } catch (e) {
          console.warn("Audio source could not be stopped:", e);
        }
        currentSource = null;
    }
};

export const playAudio = async (base64Audio: string, rate: number, onEndCallback: () => void): Promise<void> => {
    stopCurrentAudio();
    
    const ctx = getAudioContext();
    if (ctx.state === 'suspended') {
        await ctx.resume();
    }
    
    const decodedBytes = decode(base64Audio);
    const audioBuffer = await decodeAudioData(decodedBytes, ctx, 24000, 1);
    
    const source = ctx.createBufferSource();
    source.buffer = audioBuffer;
    source.playbackRate.value = rate;
    source.connect(ctx.destination);
    
    source.onended = () => {
        if (currentSource === source) {
            currentSource = null;
            onEndCallback();
        }
    };
    
    source.start();
    currentSource = source;
};

export const togglePauseResumeAudio = (): boolean => {
    const ctx = getAudioContext();
    if (ctx.state === 'running') {
        ctx.suspend();
        return true; // is now paused
    } else if (ctx.state === 'suspended') {
        ctx.resume();
        return false; // is now playing
    }
    // Fix: A closed context is not playing, so consider it paused.
    // This also resolves a TypeScript comparison error where it knew the state could only be 'closed' here.
    return true;
};


